function custom.add_smart()
    local adder = {
        indicators = { "Â¹", "Â²", "Â³", "â´", "âµ", "â¶", "â·", "â¸", "â¹" },
    }

    ---è®¡ç®—å½“å‰è¾“å…¥ä¸²çš„é€ è¯ç»“æœ
    function adder:get_word(args, env)
        local chars, codes, index = {}, {}, nil

        -- éå†å½“å‰è¾“å…¥çš„å•å­—ç¼–ç 
        for _, code in ipairs(args) do
            if string.match(code, "^[a-z][1-3][1-9]$") or string.match(code, "^[a-z][a-z][a-z1-3][1-9]$") then
                -- å¸¦æœ‰æ•°å­—é€‰é‡çš„å•å­—ç¼–ç 
                index = tonumber(string.sub(code, #code)) or 1
                code = string.sub(code, 1, #code - 1)
            elseif string.match(code, "^[a-z][a-z]?$") then
                -- ä¸å¸¦ç®€ç é”®çš„ä¸€äºŒç®€å•å­—
                index = nil
                code = code .. "1"
            else
                -- ä¸å¸¦é€‰é‡çš„å•å­—å…¨ç 
                index = nil
            end

            -- æŸ¥è¯¢å½“å‰å•å­—
            local entries = LlCore.dict_lookup(env, LlCore.base_mem, code, index or 1)
            local char = entries[index or 1] and entries[index or 1].text or ""
            if #char ~= 0 then
                -- å½•å…¥å•å­—åŠå…¶ç¼–ç 
                table.insert(chars, char)
                table.insert(codes, code)
            end
        end

        return chars, codes, index
    end

    ---æ˜¾ç¤ºé€ è¯ç»“æœ
    function adder:peek(args, env)
        local chars, codes, index = self:get_word(args, env)
        if #chars == 0 or index then
            -- æ— å•å­—, æˆ–æœ€æœ«å•å­—æœ‰é€‰é‡é”®, ç›´æ¥è¿”å›
            return table.concat(chars)
        else
            -- æ˜¾ç¤ºæœ€æœ«å•å­—çš„å€™é€‰
            local entries = LlCore.dict_lookup(env, LlCore.base_mem, codes[#codes], 9)
            local cands = ""
            for i, entry in ipairs(entries) do
                -- æœ€å¤šæ˜¾ç¤ºä¹é‡
                if i > 9 then break end
                cands = cands .. entry.text .. self.indicators[i]
            end
            return string.format("%s[%s]", table.concat(chars, "", 1, #chars - 1), cands)
        end
    end

    ---å°†é€ è¯ç»“æœæ·»åŠ åˆ°è¯åº“
    function adder:eval(args, env)
        local chars, codes = self:get_word(args, env)
        local weight = os.time()
        for i = 1, #chars - 1, 1 do
            local code, word = codes[i], chars[i]
            for j = i + 1, #chars, 1 do
                code = code .. codes[j]
                word = word .. chars[j]
                LlCore.word_trie:update(code, word, weight)
            end
        end
        return ""
    end

    return adder
end

---æ‰‹åŠ¨åˆ è¯
function custom.del_smart()
    local deleter = {
        indicators = { "Â¹", "Â²", "Â³", "â´", "âµ", "â¶", "â·", "â¸", "â¹" },
    }

    ---æŸ¥çœ‹å½“å‰å‘½ä¸­çš„å¾…åˆ æ™ºèƒ½è¯
    function deleter:peek(args)
        if #args ~= 0 then
            -- é¦–ä¸ªå‚æ•°ä¸ºç¼–ç , ç¬¬äºŒä¸ªå‚æ•°ä¸ºé€‰é‡
            local words = LlCore.word_trie:query(args[1], nil, 9)
            local index
            if #args > 1 then
                index = args[2] and tonumber(args[2]) or 1
            end

            if index then
                -- æ˜¾ç¤ºé€‰ä¸­çš„è¯
                return words[index] or ""
            else
                -- æ˜¾ç¤ºç¬¦åˆç¼–ç çš„å€™é€‰
                local cands = ""
                for i, word in ipairs(words) do
                    -- æœ€å¤šæ˜¾ç¤ºä¹é‡
                    if i > 9 then break end
                    cands = cands .. word .. self.indicators[i]
                end
                return cands
            end
        end
        return ""
    end

    ---åˆ é™¤é€‰å®šçš„æ™ºèƒ½è¯
    function deleter:eval(args)
        if #args ~= 0 then
            local code, index = args[1], args[2] and tonumber(args[2]) or 1
            local words = LlCore.word_trie:query(code, nil, 9)
            LlCore.word_trie:delete(code, words[index] or "")
        end
        return ""
    end

    return deleter
end

---å¯¼å‡ºæ™ºèƒ½è¯åº“
function custom.export_smart()
    ---è·å–æ•°æ®åº“å¯¹è±¡
    local db = LlCore.word_trie:db()
    if db then
        ---æ‰“å¼€å¤‡ä»½æ–‡ä»¶
        local userdir = rime_api.get_user_data_dir()
        local dbname = db:fetch("\x01/db_name")
        local file, err = io.open(string.format("%s/%s.userdb.bak", userdir, dbname), "w")
        if not file then
            return err
        end

        ---è¿­ä»£æ•°æ®å¹¶å†™å…¥åˆ°å¤‡ä»½æ–‡ä»¶
        local count = 0
        for key, value in db:query(":"):iter() do
            key = string.sub(key, 2, -1)
            if #key ~= 0 then
                count = count + 1
                file:write(string.format("%s\t%s\n", key, value))
            end
        end

        file:close()
        return string.format("exported %d phrases", count)
    else
        return "cannot open smart db"
    end
end

---å¯¼å…¥æ™ºèƒ½è¯åº“
function custom.import_smart()
    ---è·å–æ•°æ®åº“å¯¹è±¡
    local db = LlCore.word_trie:db()
    if db then
        ---æ‰“å¼€å¤‡ä»½æ–‡ä»¶
        local userdir = rime_api.get_user_data_dir()
        local dbname = db:fetch("\x01/db_name")
        local file, err = io.open(string.format("%s/%s.userdb.bak", userdir, dbname), "r")
        if not file then
            return err
        end

        ---ä»å¤‡ä»½æ–‡ä»¶è¯»å–æ•°æ®å¹¶å…¥åº“
        local count = 0
        for line in file:lines() do
            local key = string.match(line, "^(.-)\t")
            if key then
                local value = string.sub(line, #key + 2, -1)
                count = count + 1
                db:update(":" .. key, value)
            end
        end

        file:close()
        return string.format("imported %d phrases", count)
    else
        return "cannot open smart db"
    end
end

