---æ–‡æœ¬å†™å…¥åˆ° txt æ–‡ä»¶, åå°† txt æ¸²æŸ“ä¸º png, å†å°† png å†…å®¹æ‹·è´åˆ°å‰ªè´´æ¿, ç„¶ååˆ é™¤æ–‡ä»¶
local function convert_text_to_image(os_name)
    local filename_txt = "/tmp/liuli_capture.txt"
    local filename_png = "/tmp/liuli_capture.png"

    -- å‘½ä»¤åºåˆ—
    local cmds = {}
    local copy_cmd = "wl-copy -o <%s"
    local pango_cmd = "pango-view --font='ChaiPUA-0.2.7, PingFang SC, Plangothic P1, Plangothic P2' --dpi=256 -qo %s %s"
    if os_name == LlCore.os_types.mac then
        copy_cmd = "/usr/local/bin/impbcopy %s"
        pango_cmd = "/opt/homebrew/bin/pango-view --font='ChaiPUA-0.2.7, PingFang SC, Plangothic P1, Plangothic P2' --dpi=256 -qo %s %s"
    elseif os_name == LlCore.os_types.windows then
        filename_txt = os.getenv("TEMP") .. "\\liuli_capture.txt"
        filename_png = os.getenv("TEMP") .. "\\liuli_capture.png"
        copy_cmd = 'powershell -command "Add-Type -AssemblyName System.Windows.Forms; $img = [System.Drawing.Image]::FromFile(\'%s\'); [System.Windows.Forms.Clipboard]::SetImage($img)"'
        pango_cmd = 'pango-view --font="ChaiPUA-0.2.7, PingFang SC, Plangothic P1, Plangothic P2" --dpi=256 -qo %s %s'
    end

    -- æ¸²æŸ“åˆ° png æ–‡ä»¶ä¸­
    --table.insert(cmds, string.format("/opt/homebrew/bin/pango-view --dpi=256 -qo %s %s", filename_png, filename_txt))
    table.insert(cmds, string.format(pango_cmd, filename_png, filename_txt))
    -- æ‹·è´å›¾åƒå†…å®¹åˆ°å‰ªè´´æ¿
    --table.insert(cmds, string.format("/usr/local/bin/impbcopy %s", filename_png))
    table.insert(cmds, string.format(copy_cmd, filename_png))
    -- åˆ æ–‡ä»¶
    table.insert(cmds, string.format("rm %s %s", filename_txt, filename_png))

    return function(text)
        -- åµŒå…¥å€™é€‰å†…å®¹å†™å…¥åˆ°æ–‡ä»¶
        local file, err = io.open(filename_txt, "w")
        if err or not file then
            return err
        end
        file:write(text)
        file:close()

        -- æ‰§è¡Œå‘½ä»¤åºåˆ—
        local ok
        for _, cmd in ipairs(cmds) do
            ok, err = os.execute(cmd)
            if not ok then
                return err
            end
        end

        return ""
    end
end

